Method.Implementation:
set tSC = $$$OK, tCreated=0, tCurrentNS = $$$LOWER($namespace)
try {
	set:pWebAppName="" pWebAppName = tCurrentNS_"/extractor"
	set tWebAppName = "/csp/"_pWebAppName
	set tProperties("NameSpace") = tCurrentNS
	set tProperties("DispatchClass") = "Demo.Extractor.RestHandler"
	set tProperties("AutheEnabled") = $$$AutheUnauthenticated
	//set tProperties("iKnowEnabled") = 1

	zn "%SYS"
	if '##class(Security.Applications).Exists(tWebAppName) {
		set tSC = ##class(Security.Applications).Create(tWebAppName, .tProperties)
		quit:$$$ISERR(tSC)
		set tCreated=1
	}

} catch (ex) {
	set tSC = ex.AsStatus()
}
zn tCurrentNS

if $$$ISERR(tSC) {
	write !,"WARNING: unable to create web application due to the following error:",!
	do $system.OBJ.DisplayError(tSC)
	write !,"You'll still have to create a web application '",tWebAppName,"' referring to Demo.Extractor.RestHandler",!
} elseif (tCreated) {
	write !,"Web Application created successfully",!
}

quit $$$OK
